import React, { useState , useEffect } from 'react';
import DoneIcon from '@mui/icons-material/Done';
import AddIcon from '@mui/icons-material/Add';
import SearchIcon from '@mui/icons-material/Search';
import axios from 'axios';
import OneNote from './OneNote';

const MainContent = ({ selectedCategory , openOneNote}) => {
    const [notes, setNotes] = useState([]);
    const [categories, setCategories] = useState([]);
    const [isCreateNoteMode, setCreateNoteMode] = useState(false);
    const [newNoteTitle, setNewNoteTitle] = useState('');
    const [newNoteContent, setNewNoteContent] = useState('');
    const [selectedNote, setSelectedNote] = useState(null); 

    useEffect(() => {
        if (selectedCategory && selectedCategory._id) {
          // Fetch notes based on the selected category
          axios.get(`http://localhost:3002/categories/${selectedCategory._id}/notes`)
            .then(response => setNotes(response.data))
            .catch(error => console.error('Error fetching notes:', error));
        }
      }, [selectedCategory]);

      const createNewNote = async (categoryId) => {
        try {
            if (!selectedCategory || !selectedCategory._id) {
                console.error('No selected category');
                return;
            }
            if (selectedCategory) {
             const response = await axios.post(
            `http://localhost:3002/categories/${selectedCategory?._id}/notes`,
            {
              title: newNoteTitle,
              content: newNoteContent,
            }
          ); 

            const { note } = response.data;
                    setNotes((prevNotes) => {
            // Make sure prevNotes is an array
            const updatedNotes = Array.isArray(prevNotes) ? [...prevNotes] : [];
            updatedNotes.push(note);
            return updatedNotes;
            });
          // Reset state to go back to the list of notes
          setNewNoteTitle('');
          setNewNoteContent('');
          toggleCreateNoteMode();
        }
        } catch (error) {
          console.error('Error creating new note:', error);
        }
      };
    
      const handleNoteClick = (note) => {
        // Open the OneNote component with the selected note
        openOneNote(note);
        setSelectedNote(note);
      };
    
  const toggleCreateNoteMode = () => {
    setCreateNoteMode(!isCreateNoteMode);
  };


  return (
    <div className="main-content-area"
    style={{
        backgroundColor:"white",
        padding: "10px",
        marginLeft:"10px",
        borderRadius:"5px",
        width:"203%",
    }}>
      {/* If a category is selected, show the list of notes */}
      {selectedCategory && !isCreateNoteMode  ? (
        <div>
          {/* Buttons for creating a new note and searching */}
          <div className="note-actions" style={{display:"flex",gap:"10px",marginBottom:"10px"}}>
            <button className="create-note-btn"
            style={{ 
                display:"flex",
                height:"22px",
                backgroundColor: '#95D03A', 
                color: "white",
                borderRadius:"3px", 
                padding:"1px 2px 1px 30px",
                fontSize:"10px", 
                alignItems:"center",
                border: 'none',
                cursor: 'pointer',
                }}
                onClick={() => { toggleCreateNoteMode(); openOneNote(null); }}
            >
            <span style={{marginRight:"23px"}}>Create Note</span>
            <div 
            style={{ 
            display: 'flex', 
            alignItems: 'center' ,
            borderLeft: '1px solid lightslategray',
        }}>
        <AddIcon style ={{color:"white",marginLeft:"2px",fontSize:"20px"}}/>
        </div></button>
         
        <div className="search-bar" style={{ position: 'relative',display:"flex" }}>
            <span className="search-icon" style={{ position: 'absolute', left: '5px',top:"10%"}}>
                <SearchIcon style={{ fontSize:"15px", color:"lightslategrey2"}}/>
            </span>
            <input type="text" placeholder="Search..." style={{ paddingLeft: '22px',fontSize:"10px",outline:"none" }} />
        </div>
        </div>
          {selectedCategory.notes.map(note => (
            <div key={note._id} className="note-item"
              style={{
                borderBottom: "1px solid lightgray",
                fontSize: "10px",
                color: "gray",
                cursor: 'pointer',
                backgroundColor: selectedNote && selectedNote._id === note._id ? "#DCE9F2" : "white",
              }}
              onClick={() => handleNoteClick(note)}>
              <h3 style={{ margin: "0px" }}>{note.title}</h3>
              <p style={{ margin: "0px", marginBottom: "5px" }}>{note.content}</p>
            </div>
          ))}
        </div>
      ):(
     
        <div className="add-note-placeholder">
          <div style={{display:"flex",justifyContent:"space-between"}}>
            <div style={{display:"flex",gap:"10px"}}>
                <div style={{backgroundColor:"#006994", width:"70px",borderRadius:"3px"}}></div>
                <div style={{backgroundColor:"#006994",width:"70px",borderRadius:"3px"}}></div>
                <div style={{backgroundColor:"#95D03A",width:"70px",borderRadius:"3px"}}></div>
            </div>
            <div  style={{display:"flex",gap:"10px"}}>
                <div style={{backgroundColor:"#006994", width:"23px",borderRadius:"3px"}}></div>
                <div style={{backgroundColor:"#006994",width:"23px",borderRadius:"3px"}}></div>
                <div style={{backgroundColor:"#006994",color:"#006994",width:"23px",borderRadius:"3px"}}>.</div>
            </div>
          </div>
          <input type="text" placeholder="Add a title"
          style={{
            padding: "9px 6px 9px 0px",
            width: "99%",
            border: "none",
            borderBottom: "1px solid lightslategray",
            outline:"none",
            fontSize:"10px",
          }}
          value={newNoteTitle}
          onChange={(e) => setNewNoteTitle(e.target.value)}
          />
          <textarea placeholder="Add a note here"
          style={{
            width: "99%",
            paddingTop: "9px",
            border: "none",
            fontSize: "10px",
            fontFamily: "sans-serif",
            paddingLeft: "0px",
            height: "433px",
            outline: "none",
            resize: "none",
          }}
          value={newNoteContent}
          onChange={(e) => setNewNoteContent(e.target.value)}></textarea>
          <button className="save-changes-btn"
           style={{ 
            display:"flex",
            backgroundColor: '#95D03A', 
            color: "white",
            borderRadius:"3px", 
            padding:"1px 2px 1px 18px",
            fontSize:"10px", 
            alignItems:"center",
            border: 'none',
            cursor: 'pointer',
            float:"right",
            }}
            onMouseEnter={(e) => e.target.style.backgroundColor = '#7FAB25'} 
            onMouseLeave={(e) => e.target.style.backgroundColor = '#95D03A'}
            onClick={() => createNewNote(selectedCategory?._id)}>
                
               <span style={{marginRight:"12px"}}> Save Changes</span>
                <div 
            style={{ 
                display: 'flex', 
                alignItems: 'center' ,
                borderLeft: '1px solid lightslategray',
            }}>
            <DoneIcon style ={{color:"white",marginLeft:"2px",fontSize:"20px",}}/>
            </div>
            </button>
        </div>
      )
      }
    </div>
  );
};

export default MainContent;
