import React, { useState , useEffect} from 'react';
import FolderIcon from '@mui/icons-material/Folder';
import ArrowDropDownRoundedIcon from '@mui/icons-material/ArrowDropDownRounded';
import ArrowRightRoundedIcon from '@mui/icons-material/ArrowRightRounded';
import AddIcon from '@mui/icons-material/Add';
import axios from 'axios';

const Sidebar = ({ selectedCategory, handleCategoryClick , selectedNote }) => {
    const [categories, setCategories] = useState([]);

    useEffect(() => {
      axios.get('http://localhost:3002/categories')
        .then(response => setCategories(response.data))
        .catch(error => console.error('Error fetching categories:', error));
    }, []);

    return (
    <div className="sidebar" 
    style={{
        height: "87vh",
        backgroundColor:"white", 
        padding: "10px",
        borderRadius:"5px",
        width: selectedNote ? "106%" : "43%",
        overflowY:"auto",
        overflowX: "hidden",
        }}>
    <button className="create-category-btn"
    style={{ 
        width:"100%",
        backgroundColor: '#95D03A', 
        color: "white",
        borderRadius:"3px", 
        marginBottom:"10px",
        display:"flex",
        padding:"1px 2px 1px 57px",
        fontSize:"10px", 
        alignItems:"center",
        justifyContent:"space-between",
        border: 'none',
        cursor: 'pointer',
        }}>
            Create Category
            <div 
            style={{ 
                display: 'flex', 
                alignItems: 'center' ,
                borderLeft: '1px solid lightslategray',
            }}><AddIcon style ={{color:"white",marginLeft:"2px",fontSize:"20px",}}/>
            </div>
    </button>
    {categories.map((category) => (
    <div
    key={category._id} className={`category ${selectedCategory?._id === category._id ? 'selected' : ''}`}
    onClick={() => handleCategoryClick(category)}
    style ={{
        borderRadius: "5px", 
        backgroundColor:selectedCategory?._id === category._id ? "#f5f5f5" : "#006994", 
        color:selectedCategory?._id === category._id ? "black" : "white", 
        marginBottom:"10px",
        display:"flex",
        padding:"2px", 
        fontSize:"10px",
        alignItems:"center",
        cursor:"pointer",
    }}>
        <span className="folder-icon"><FolderIcon style ={{color: selectedCategory?._id === category._id ? "black" : "white"}}/></span>
        <span className="category-name" style ={{marginLeft:"2px",width:"90px"}}>{category.name}{`(${category.notes.length})`}</span>
        <span className="arrow-icon">{selectedCategory?._id === category._id ? 
        <ArrowRightRoundedIcon style ={{color:"black",marginLeft:"40px"}}/> : 
        <ArrowDropDownRoundedIcon style ={{color:"white", marginLeft:"40px"}}/>}
        </span>
    </div>
))}
</div>
);
};

export default Sidebar;
