import React, { useState, useEffect } from "react";
import CancelIcon from "@mui/icons-material/Cancel";
import DoneIcon from "@mui/icons-material/Done";
import DeleteIcon from "@mui/icons-material/Delete";

const OneNote = ({ selectedNote, onCancel, onSaveChanges }) => {
  const [editedTitle, setEditedTitle] = useState("");
  const [editedContent, setEditedContent] = useState("");

  useEffect(() => {
    if (selectedNote) {
      setEditedTitle(selectedNote.title);
      setEditedContent(selectedNote.content);
    }
  }, [selectedNote]);

  const handleCancel = () => {
    onCancel();
  };

  const handleSaveChanges = () => {
    onSaveChanges(editedTitle, editedContent);
  };

  // Render null if selectedNote is not available
  if (!selectedNote) {
    return null;
  }

  return (
    <OneNoteContainer>
      <div className="add-note-placeholder">
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <div style={{ display: "flex", gap: "10px" }}>
            <div
              style={{
                backgroundColor: "#006994",
                width: "70px",
                borderRadius: "3px",
              }}
            ></div>
            <div
              style={{
                backgroundColor: "#006994",
                width: "70px",
                borderRadius: "3px",
              }}
            ></div>
            <div
              style={{
                backgroundColor: "#95D03A",
                width: "70px",
                borderRadius: "3px",
              }}
            ></div>
          </div>
          <div style={{ display: "flex", gap: "10px" }}>
            <div
              style={{
                backgroundColor: "#006994",
                width: "23px",
                borderRadius: "3px",
              }}
            ></div>
            <div
              style={{
                backgroundColor: "#006994",
                width: "23px",
                borderRadius: "3px",
              }}
            ></div>
            <div
              style={{
                backgroundColor: "#006994",
                color: "#006994",
                width: "23px",
                borderRadius: "3px",
              }}
            >
              .
            </div>
          </div>
        </div>
      </div>
      <input
        type="text"
        placeholder="Add a title"
        style={{
          padding: "9px 6px 9px 0px",
          width: "99%",
          border: "none",
          borderBottom: "1px solid lightslategray",
          outline: "none",
          fontSize: "10px",
        }}
        value={editedTitle}
        onChange={(e) => setEditedTitle(e.target.value)}
      />
      <textarea
        placeholder="Add a note here"
        style={{
          width: "99%",
          paddingTop: "9px",
          border: "none",
          fontSize: "10px",
          fontFamily: "sans-serif",
          paddingLeft: "0px",
          height: "433px",
          outline: "none",
          resize: "none",
        }}
        value={editedContent}
        onChange={(e) => setEditedContent(e.target.value)}
      ></textarea>
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <button
          className="save-changes-btn"
          style={{
            display: "flex",
            backgroundColor: "#EF6262",
            color: "white",
            borderRadius: "3px",
            padding: "1px 2px 1px 18px",
            fontSize: "10px",
            alignItems: "center",
            border: "none",
            cursor: "pointer",
          }}
          onMouseEnter={(e) => (e.target.style.backgroundColor = "#E06469")}
          onMouseLeave={(e) => (e.target.style.backgroundColor = "#EF6262")}
          onClick={handleCancel}
        >
          <span style={{ marginRight: "12px" }}> Delete Note</span>
          <div
            style={{
              display: "flex",
              alignItems: "center",
              borderLeft: "1px solid lightslategray",
            }}
          >
            <DeleteIcon
              style={{ color: "white", marginLeft: "2px", fontSize: "20px" }}
            />
          </div>
        </button>
        <button
          className="save-changes-btn"
          style={{
            display: "flex",
            backgroundColor: "#95D03A",
            color: "white",
            borderRadius: "3px",
            padding: "1px 2px 1px 18px",
            fontSize: "10px",
            alignItems: "center",
            border: "none",
            cursor: "pointer",
          }}
          onMouseEnter={(e) => (e.target.style.backgroundColor = "#7FAB25")}
          onMouseLeave={(e) => (e.target.style.backgroundColor = "#95D03A")}
          onClick={handleSaveChanges}
        >
          <span style={{ marginRight: "12px" }}> Save Changes</span>
          <div
            style={{
              display: "flex",
              alignItems: "center",
              borderLeft: "1px solid lightslategray",
            }}
          >
            <DoneIcon
              style={{ color: "white", marginLeft: "2px", fontSize: "20px" }}
            />
          </div>
        </button>
      </div>
    </OneNoteContainer>
  );
};

export default OneNote;

const OneNoteContainer = styled.div`
  background-color: white;
  padding: 10px;
  margin-left: 10px;
  border-radius: 5px;
  width: 1010px;
`;
